      <Table striped bordered hover variant="dark">
      <thead>
        <tr>
        <th>ID</th>
          <th>Name</th>
          <th>Email</th>
          <th>Contact</th>
          <th>Address</th>
        </tr>
        </thead>
        <tbody>
      
    {
       obj.map((element,index)=>{
       return(
        <tr key={index}>
         <td>{index+1}</td>
          <td>{element.name}</td>
          <td>{element.email}</td>
          <td>{element.contact}</td>
          <td>
         <Table striped bordered hover variant="dark">
         <tbody>
            {
                element.address.map((item)=>{
                   return(
                    <tr>
                    <td>{item.houseNo}</td>
                  <td>{item.city}</td>
                  <td>{item.country}</td>
                    </tr>
                   )
                })
            }
            </tbody>
            </Table>
          </td>
        </tr>
       )
       })
    }
        
        </tbody>
      </Table>
    </>
  );
};
*/

