        return(
            <>
                <h1>Count is {this.state.count}</h1>
                <button onClick={()=> this.setState({count:1})}>click me</button>
            </>
        )
    }
}
*/

// useMemo Hook
// this is used to prevent the unwanted call of functions or unwanted re-rendering of component
// When i click on update count then function called and then i click on update item agin the function called which is un wanted to prevent this we use memo and tell that only when count updated this function called.
// first we use this <h2>{countUpdate()}</h2> then after memo  <h2>{newUseMemo}</h2>
/*
import React, { useState,useMemo } from "react";

const App =()=>{
    const [count,setCount] = useState(1);
    const [item,setItem] = useState(1);

const newUseMemo = useMemo( function countUpdate(){
    console.log("countUpdate")
    return count*5;
},[count])
   

    return(
        <>
            <h1>Hello</h1>
            <h2>count is {count}</h2>
            <h2>item is {item}</h2>
            <h2>{newUseMemo}</h2>
            <button onClick={()=>setCount(count+1)}>Update count</button> <br/>
            <button onClick={()=>setItem(item*10)}>Update item</button>
        </>
    )
}
*/

