// foreach, map, filter, find, findIndex, splice, slice, reduce 

import React from "react";
import { useState } from "react";

const App=()=>{

  let [data, setData] = useState([
    { id: 1, name: "watch" },
    { id: 2, name: "bag" },
    { id: 3, name: "shoes" },
    { id: 3, name: "bag" },
  ]);


  function getWatch(){

   let watchItem = data.filter((item, index)=>{
        return item.name === "watch"
    })
  }
    
  return (
    <>
      <h1>Hello</h1>
      <button onClick={getWatch()}>get watch</button>
      <p>{data}</p>
      
    </>
  );
}

export default App;
